toICalDate = date => {
  date = date
    .toISOString() // base format
    .replace(/-|:|\./g, ""); // remove separators

  return date.substring(0, date.length - 4); // remove Z, excess 0s
};

// store current time
msg.now = toICalDate(new Date());

const events = [];

// get event
for (const event of msg.payload) {
    if (!!event) {
        // format duration
        event.duration = `PT${event.duration}M`;

        // format start
        event.start = toICalDate(new Date(event.start));

        events.push(event);
    }
}

if (events.length > 0) {
    msg.payload = events;
    return [msg, null];
} else {
    return [null, msg];
}

